let Length = function ( value, unitPrefix ) {
    this.value = value;
};

Length.units = [ ];
Length.listNames = { };
Length.listSymbols = { };

Length.units.push({
    name: 'meter',
    symbol: 'm',
    unitPrefix: true,
    SI: true,
});
Length.listNames.meter = Length.units.at(-1);
Length.listSymbols.m = Length.units.at(-1);

Length.units.push({
    name: 'kmes',
    symbol: 'kme',
});
Length.listNames.kmes = Length.units.at(-1);
Length.listSymbols.kme = Length.units.at(-1);

const listNames = Object.keys(Length.listSymbols);
listNames.sort( (a,b) => b.length - a.length );
Length.startWith = function( postfix ) {
    for(let i=0; i<listNames.length; i++) {
        if( postfix.startsWith( listNames[i] )) {
            const l = listNames[i].length;
            return [ postfix.slice(0, l), postfix.slice(l) ];
        }
    }
    return [ undefined, postfix ];
};

module.exports = Length;
