const listNames = Object.keys(UnitPrefix.listSymbols);
listNames.sort( (a,b) => b.length - a.length );
UnitPrefix.startWith = function( prefix ) {
    for(let i=0; i<listNames.length; i++) {
        if( prefix.startsWith( listNames[i] )) {
            const l = listNames[i].length;
            return [ prefix.slice(0, l), prefix.slice(l) ];
        }
    }
    return [ undefined, prefix ];
};

