const Unit = require('./PhysicUnit/Unit.js');
const ComplexUnit = require('./PhysicUnit/ComplexUnit.js');

const Mass = require('./Mass.js');
const Length = require('./Length.js');
const Time = require('./Time.js');
const Force = require('./Force.js');

let Pressure = function ( ) { };
let NewUnit = Pressure;

NewUnit.prototype = Unit;
Unit.call( NewUnit );


NewUnit.push({
    name: 'Pascal',
    symbol: 'Pa',
    defaultPower: 1,
    power: 1,
    conversionLvl: 1000,
    toMain: 1,
    fromMain: 1,

    isCombineType: true,
    combine: new ComplexUnit([
        [Force.bySymbols.N,  1],
        [Length.bySymbols.m, 1],
        [Time.bySymbols.s,   2],
    ]),

    isPrefixSupport: true,
});

NewUnit.update();

module.exports = Pressure;
